            // Check if computed parity matches expected parity
            if (parity_bit !== expected_parity) begin
                $display("Error with input %b: expected %b, got %b", data, expected_parity, parity_bit);
                error_flag = 1;
            end
        end
        if (error_flag === 0)
            $display("all test cases passed");
    end
