                if ((j == 0 && k == 0 && Q !== test_vector[i][0]) ||
                    (j == 0 && k == 1 && Q !== 0) ||
                    (j == 1 && k == 0 && Q !== 1) ||
                    (j == 1 && k == 1 && Q !== ~test_vector[i][0])) begin
