module testbench;
    // Inputs to the DUT
    reg clk;
    reg areset;
    reg bump_left;
    reg bump_right;
    reg ground;
    reg dig;

    // Outputs from the DUT
    wire walk_left;
    wire walk_right;
    wire aaah;
    wire digging;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .dig(dig),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah),
        .digging(digging)
    );

    // Clock generation
    initial begin
        clk = 1'b0;
        forever #5 clk = ~clk; // Generate a clock with 10ns period
    end

    // Test scenarios
    initial begin
        // Initialize all inputs
        areset = 1;
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;

        // Assert reset at the beginning to set initial state
        #(10);
        areset = 0;
        #(10);
        areset = 1;
        #(10);
        areset = 0;

        // Check initial conditions
        if (walk_left !== 1 || walk_right !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test case failed");
            $finish;
        end

        // Case 1: Lemming walking left hits left bump
        #(20) bump_left = 1;
        #(10) bump_left = 0;
        if (walk_right !== 1) begin
            $display("Test case failed");
            $finish;
        end

        // Case 2: Lemming now walking right hits right bump
        #(20) bump_right = 1;
        #(10) bump_right = 0;
        if (walk_left !== 1) begin
            $display("Test case failed");
            $finish;
        end

        // Case 3: Lemming walking left, ground disappears
        #(20) ground = 0;
        if (aaah !== 1) begin
            $display("Test case failed");
            $finish;
        end
        // Lemming should continue "aaah" for 20 cycles
        #(200);
        // Ground reappears
        ground = 1;
        if (walk_left !== 1 || aaah !== 0) begin
            $display("Test case failed");
            $finish;
        end

        // Case 4: Start digging
        #(20) dig = 1;
        if (digging !== 1) begin
            $display("Test case failed");
            $finish;
        end
        // Finish digging
        #(20) ground = 0;
        if (aaah !== 1 || digging !== 0) begin
            $display("Test case failed");
            $finish;
        end

        // Check for splatting after falling too long
        #(420) ground = 1; // More than 20cycles * 10ns
        if (walk_left !== 0 || walk_right !== 0 || aaah !== 0 || digging !== 0) begin
            $display("Test case failed");
            $finish;
        end

        // Final reset to walk left
        #(20);
        areset = 1;
        #(10);
        areset = 0;
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Test case failed");
            $finish;
        end

        // All test cases passed
        $display("all test cases passed");
        $finish;
    end
endmodule

