module testbench;
    // Inputs
    reg in;
    reg [3:0] state;

    // Outputs
    wire [3:0] next_state;
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Test variables
    integer i;
    reg [3:0] expected_state;
    reg expected_out;
    reg all_passed;

    // Test procedure
    initial begin
        // Initialize inputs
        in = 0;
        state = 4'b0001; // Start at state A
        all_passed = 1;

        // Apply test cases for every possible input and state combination
        for (i = 0; i < 8; i = i + 1) begin
            #10; // Delay to observe changes

            // Determine the expected outputs based on the current input and state
            case(state)
                4'b0001: begin // State A
                    expected_state = in ? 4'b0010 : 4'b0001;
                    expected_out = 0;
                end
                4'b0010: begin // State B
                    expected_state = in ? 4'b0010 : 4'b0100;
                    expected_out = 0;
                end
                4'b0100: begin // State C
                    expected_state = in ? 4'b1000 : 4'b0001;
                    expected_out = 0;
                end
                4'b1000: begin // State D
                    expected_state = in ? 4'b0010 : 4'b0100;
                    expected_out = 1;
                end
                default: begin
                    expected_state = 4'b0001;
                    expected_out = 0;
                end 
            endcase

            // Check if the DUT outputs match the expected outputs
            if (next_state !== expected_state || out !== expected_out) begin
                all_passed = 0; // Set flag low if any mismatch occurs
            end

            // Update the state for the next loop iteration
            state = expected_state;
            in = ~in; // Toggle input to test different transitions
        end

        // Display results after all test cases
        if (all_passed) begin
            $display("all test cases passed");
        end

        // Finish the simulation
        $finish;
    end
endmodule

