    initial begin
        clk = 0;
        test_vector = 5'b00000; // Initial state: x = 0, previous state = 0 (reset) 

        for (i = 0; i < 32; i = i + 1) begin
            #10;
            x = test_vector[0];
            test_vector = {test_vector[0], z, test_vector[2:1]};

            // Check against expected output
            case (test_vector[4:1])
                // State set based on last x input and previous z outputs
                4'b0000: if (z !== 1'b1) fail();
                4'b0001: if (z !== 1'b1) fail();
                4'b0010: if (z !== 1'b1) fail();
                4'b0011: if (z !== 1'b0) fail();
                4'b0100: if (z !== 1'b0) fail();
