module testbench;
    reg clk;
    reg enable;
    reg S;
    reg A;
    reg B;
    reg C;
    wire Z;

    top_module dut (
        .clk(clk),
        .enable(enable),
        .S(S),
        .A(A),
        .B(B),
        .C(C),
        .Z(Z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with a period of 10ns
    end

    // Function to display results based on comparison
    task display_results;
        input reg[7:0] expected_pattern;
        input reg[2:0] vector;
        begin
            {A, B, C} = vector;
            #10; // Delay for stabilization
            if(Z !== expected_pattern[vector]) begin
                $display("Test case failed for input combination ABC = %b", vector);
                return;
            end
        end
    endtask

    integer i;
    initial begin
        enable = 0; S = 0; {A, B, C} = 3'b000;
        #10 enable = 1;

        // Scenario 1: Shift in 10101010, then read for all combinations of ABC
        {S} = 1; #10; // Shift in 1st bit, MSB
        {S} = 0; #10; // Shift in 2nd bit
        {S} = 1; #10; // Shift in 3rd bit
        {S} = 0; #10; // Shift in 4th bit
        {S} = 1; #10; // Shift in 5th bit
        {S} = 0; #10; // Shift in 6th bit
        {S} = 1; #10; // Shift in 7th bit
        {S} = 0; #10; // Shift in 8th bit, LSB

        // Disable shifting
        enable = 0;

        // Read all combinations and check output
        for (i = 0; i <= 7; i = i + 1) begin
            display_results(8'b10101010, i);
        end

        // Check if all tests passed without any failed logs
        $display("all test cases passed");

        $finish;
    end
endmodule

