    top_module DUT (
        .a(a), 
        .b(b), 
        .out_assign(out_assign), 
        .out_alwaysblock(out_alwaysblock)
    );

    // Initialize all variables
    initial begin
        a = 0; b = 0;
    end

    // Test case stimulus
    initial begin
        // Test case 1
        #10 a = 0; b = 0;
        #10 check_results(0, 0, "Case 0,0");

        // Test case 2
        #10 a = 0; b = 1;
        #10 check_results(0, 0, "Case 0,1");

        // Test case 3
        #10 a = 1; b = 0;
        #10 check_results(0, 0, "Case 1,0");

        // Test case 4
        #10 a = 1; b = 1;
        #10 check_results(1, 1, "Case 1,1");

        // Check if all test cases passed
        #10 if (passed == 4) $display("all test cases passed");
        #10 $finish;
    end

    // Variables to keep track of number of passed test cases
    integer passed = 0;

    // Task to check results, update passed count
    task check_results;
        input expected_assign;
        input expected_alwaysblock;
        input [31*8:0] test_case;

        begin
            if (out_assign === expected_assign && out_alwaysblock === expected_alwaysblock) begin
                passed = passed + 1;
            end else begin
                $display("%s FAILED: Expected assign: %b, always block: %b; Got assign: %b, always block: %b", test_case, expected_assign, expected_alwaysblock, out_assign, out_alwaysblock);
            end
        end
    endtask
endmodule

