    wire out;
    reg [1:0] test_case;
    reg error_detected;

    // Instance of the DUT
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk; // Generate a clock with period 10ns
    end

    // Stimulus and test logic
    initial begin
        error_detected = 0;
        test_case = 0;
        // Reset the device
        reset = 1'b1; 
        #20; 
        reset = 1'b0;

        // Test case 0: B (out=1) --in=0--> A
        @(negedge clk) in = 0; reset = 0;
        @(posedge clk); // Wait for one clock cycle
        if (out !== 1) error_detected = 1;

        // Test case 1: A (out=0) --in=0--> B
        @(negedge clk) in = 0;
        @(posedge clk);
        if (out !== 0) error_detected = 1;

        // Test case 2: B (out=1) --in=1--> B
        @(negedge clk) in = 1;
        @(posedge clk);
        if (out !== 1) error_detected = 1;

        // Test case 3: A (out=0) --in=1--> A
        // Reset to state A (out=0)
        @(negedge clk) reset = 1; in = 0;
        @(posedge clk) reset = 0;
        @(posedge clk); // Let state stabilize at A
        @(negedge clk) in = 1;
        @(posedge clk);
        if (out !== 0) error_detected = 1;

        // Final evaluation
        if (error_detected === 0)
            $display("all test cases passed");

        // Finish the simulation
        $finish;
    end
