        // Initialize inputs
        areset = 1;
        bump_left = 0;
        bump_right = 0;
        ground = 1;
        dig = 0;
        #5 areset = 0;  // End of reset

        // Test Case 1: Walk left to walk right transition when bumped on left
        #20 bump_left = 1;
        #20 bump_left = 0;

        // Test Case 2: Walk right to walk left transition when bumped on right
        #40 bump_right = 1;
        #20 bump_right = 0;

        // Test Case 3: Fall and say "aaah!" when ground disappears
        #60 ground = 0;
        #40 ground = 1;

        // Test Case 4: Digging on ground and fall
        #60 dig = 1;
        #80 ground = 0;
        #40 ground = 1;
        #10 dig = 0;

