module tb_top_module;

  reg [2:0] in;           // Input to the DUT
  wire [1:0] out;         // Output from the DUT
  integer i;              // Counter for the loop
  integer num_passed;     // Counter for passed test cases

  // Instantiate the Device Under Test (DUT)
  top_module dut(
    .in(in),
    .out(out)
  );

  // Test vector and expected result
  reg [2:0] test_vectors[0:7];        // input test vectors
  reg [1:0] expected_outputs[0:7];    // expected outputs for each vector

  initial begin
    // Initialize test vectors and their expected results
    test_vectors[0] = 3'b000; expected_outputs[0] = 2'b00;
    test_vectors[1] = 3'b001; expected_outputs[1] = 2'b01;
    test_vectors[2] = 3'b010; expected_outputs[2] = 2'b01;
    test_vectors[3] = 3'b011; expected_outputs[3] = 2'b10;
    test_vectors[4] = 3'b100; expected_outputs[4] = 2'b01;
    test_vectors[5] = 3'b101; expected_outputs[5] = 2'b10;
    test_vectors[6] = 3'b110; expected_outputs[6] = 2'b10;
    test_vectors[7] = 3'b111; expected_outputs[7] = 2'b11;

    num_passed = 0; // Initialize the pass counter

    // Apply test vectors
    for (i = 0; i < 8; i = i + 1) begin
      in = test_vectors[i];  // Set the input
      #10;                   // Wait for the output to stabilize

      if (out !== expected_outputs[i]) begin
        $display("Test case %d failed: input = %b, expected output = %b, received output = %b", i, in, expected_outputs[i], out);
      end else begin
        num_passed = num_passed + 1; // Increment pass counter if the test case is successful
      end
    end

    // Check if all test cases are passed
    if (num_passed == 8) begin
      $display("all test cases passed");
    end

    $finish; // Stop the simulation
  end

