    clk = 0;
    repeat (100) #5 clk = ~clk; // Toggle clock every 5 ns for a limited number of cycles to simulate for 1000ns
    $finish; // Finish simulation after a specific time
end

// Stimulus
initial begin
    // Reset pulse
    reset = 1'b1;
    #10 reset = 1'b0;

    // Test vectors    
    @(negedge reset); // Wait for reset to deactivate

    // State A tests
    w = 1'b1; @(posedge clk); // A->A, z should be 0
    check_output(0, "Test A-1 Failure");
    w = 1'b0; @(posedge clk); // A->B, z should be 0
    check_output(0, "Test A-2 Failure");

    // State B tests
    w = 1'b1; @(posedge clk); // B->D, z should be 0
    check_output(0, "Test B-1 Failure");
    w = 1'b0; @(posedge clk); // D->F (from previous input), z should be 0
    check_output(0, "Test B-2 Failure");

    // State F tests
    w = 1'b1; @(posedge clk); // F->D, z should be 1
    check_output(1, "Test F-1 Failure");
    w = 1'b0; @(posedge clk); // D->F, z should be 0
    check_output(0, "Test F-2 Failure");

    // State F tests more
    w = 1'b0; @(posedge clk); // F->C, z should be 1
    check_output(1, "Test F-3 Failure");
    w = 1'b1; @(posedge clk); // C->D, z should be 0
    check_output(0, "Test F-4 Failure");

    // State D to A check
