    check_output(0, "Test D-A Failure");

    // Final checks (wrap up repetitive sequences if needed)
    // Now reset and check for initial state behavior
    reset = 1'b1;
    @(posedge clk);
    reset = 1'b0;
    w = 1'b1; @(posedge clk); // A->A, z should be 0 again
    check_output(0, "Reset Test Failure");

    $display("all test cases passed");
end

