    reg [7:0] in;
    wire [31:0] out;
    integer i;
    reg [5:0] testcase; // Declare a 6-bit register array to track the outcomes of the test cases

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .out(out)
    );

    initial begin
        // Initialize the testcase register
        testcase = 6'b000000; // Each bit represents a test case, initialized to 0 (fail)

        // Test cases
        in = 8'b01010101; #10;  // Test case 1
        if (out !== 32'h00000055) 
            $display("Test case failed for input 01010101");
        else
            testcase[0] = 1'b1; // Mark test case 1 as passed

        in = 8'b10101010; #10;  // Test case 2
        if (out !== 32'hFFFFFFAA) 
            $display("Test case failed for input 10101010");
        else
            testcase[1] = 1'b1; // Mark test case 2 as passed

        in = 8'b00000000; #10;  // Test case 3
        if (out !== 32'h00000000) 
            $display("Test case failed for input 00000000");
        else
            testcase[2] = 1'b1; // Mark test case 3 as passed

        in = 8'b11111111; #10;  // Test case 4
        if (out !== 32'hFFFFFFFF) 
            $display("Test case failed for input 11111111");
        else
            testcase[3] = 1'b1; // Mark test case 4 as passed

        in = 8'b01111111; #10;  // Test case 5
        if (out !== 32'h0000007F) 
            $display("Test case failed for input 01111111");
        else
            testcase[4] = 1'b1; // Mark test case 5 as passed

        in = 8'b10000000; #10;  // Test case 6
        if (out !== 32'hFFFFFF80) 
            $display("Test case failed for input 10000000");
        else
            testcase[5] = 1'b1; // Mark test case 6 as passed

        // Check all test cases passed
        #10;
        // Summarize results
        for (i = 0; i < 6; i++) begin
            if (testcase[i])
                $display("Test case %d passed", i+1);
            else begin
                $display("Test case %d failed", i+1);
                $finish; // Terminate simulation on any failure
            end
        end
        $display("All test cases passed");
    end
endmodule

