module tb_top_module;

    // Testbench variables
    reg d;
    reg ena;
    wire q;

    // Device under test
    top_module dut(
        .d(d),
        .ena(ena),
        .q(q)
    );

    // function to display result based on test case evaluation
    integer num_tests = 6; // total tests
    integer pass_count = 0; // passed tests

    initial begin
        // Test case 1: Check q holds value when ena = 0
        ena = 0; d = 1; #10;
        ena = 0; d = 0; #10;
        if (q == 1) pass_count = pass_count + 1;

        // Test case 2: Check q changes to 0 when ena enabled
        ena = 1; d = 0; #10;
        if (q == 0) pass_count = pass_count + 1;

        // Test case 3: Check q changes to 1 when ena enabled
        ena = 1; d = 1; #10;
        if (q == 1) pass_count = pass_count + 1;

        // Test case 4: Check q holds value when ena = 0
        ena = 0; d = 1; #10;
        if (q == 1) pass_count = pass_count + 1;

        // Test case 5: Alternate to ensure reliability
        d = 0; ena = 1; #10;
        d = 1; ena = 1; #10;
        d = 0; ena = 1; #10;
        d = 1; ena = 1; #10;
        d = 0; ena = 1; #10;
        d = 1; ena = 1; #10;
        d = 0; ena = 0; #10;
        if (q == 1) pass_count = pass_count + 1;

        // Test case 6: Make sure q can hold when d changes while ena = 0
        ena = 0; d = 1; #10;
        d = 0; #10;
        d = 1; #10;
        if (q == 1) pass_count = pass_count + 1;

        // Check if all test cases passed
        if (pass_count == num_tests) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end

endmodule

