module testbench;
    reg [3:0] in;
    wire [1:0] pos;
    integer i;
    reg [1:0] expected_pos;
    reg test_failed = 0;

    top_module dut (.in(in), .pos(pos));

    initial begin
        // Test all combinations for a 4-bit input
        for (i = 0; i < 16; i = i + 1) begin
            in = i;
            #10; // Wait 10ns for the output to stabilize

            // Calculate expected position of the first '1'
            case (in)
                4'b0001: expected_pos = 0;
                4'b0010: expected_pos = 1;
                4'b0100: expected_pos = 2;
                4'b1000: expected_pos = 3;
                4'b0011: expected_pos = 0;
                4'b0101: expected_pos = 0;
                4'b0110: expected_pos = 1;
                4'b0111: expected_pos = 0;
                4'b1001: expected_pos = 0;
                4'b1010: expected_pos = 1;
                4'b1011: expected_pos = 0;
                4'b1100: expected_pos = 2;
                4'b1101: expected_pos = 0;
                4'b1110: expected_pos = 1;
                4'b1111: expected_pos = 0;
                default: expected_pos = 0; // Input is 0 or no 1's are present
            endcase

            if (pos !== expected_pos) begin
                $display("Test failed for input %b. Expected %d, got %d.", in, expected_pos, pos);
                test_failed = 1;
            end
        end

        if (test_failed === 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

