module testbench;
    reg x3, x2, x1;
    wire f;
    integer errors;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .x3(x3),
        .x2(x2),
        .x1(x1),
        .f(f)
    );

    initial begin
        // Initialize variables
        errors = 0;

        // Test Case 1: x3 = 0, x2 = 0, x1 = 0, Expected f = 0
        {x3, x2, x1} = 3'b000;
        #10;
        if(f !== 1'b0) begin
            $display("Test case failed: x3=0, x2=0, x1=0, Expected f=0, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 2: x3 = 0, x2 = 0, x1 = 1, Expected f = 0
        {x3, x2, x1} = 3'b001;
        #10;
        if(f !== 1'b0) begin
            $display("Test case failed: x3=0, x2=0, x1=1, Expected f=0, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 3: x3 = 0, x2 = 1, x1 = 0, Expected f = 1
        {x3, x2, x1} = 3'b010;
        #10;
        if(f !== 1'b1) begin
            $display("Test case failed: x3=0, x2=1, x1=0, Expected f=1, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 4: x3 = 0, x2 = 1, x1 = 1, Expected f = 1
        {x3, x2, x1} = 3'b011;
        #10;
        if(f !== 1'b1) begin
            $display("Test case failed: x3=0, x2=1, x1=1, Expected f=1, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 5: x3 = 1, x2 = 0, x1 = 0, Expected f = 0
        {x3, x2, x1} = 3'b100;
        #10;
        if(f !== 1'b0) begin
            $display("Test case failed: x3=1, x2=0, x1=0, Expected f=0, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 6: x3 = 1, x2 = 0, x1 = 1, Expected f = 1
        {x3, x2, x1} = 3'b101;
        #10;
        if(f !== 1'b1) begin
            $display("Test case failed: x3=1, x2=0, x1=1, Expected f=1, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 7: x3 = 1, x2 = 1, x1 = 0, Expected f = 0
        {x3, x2, x1} = 3'b110;
        #10;
        if(f !== 1'b0) begin
            $display("Test case failed: x3=1, x2=1, x1=0, Expected f=0, Got f=%b", f);
            errors = errors + 1;
        end

        // Test Case 8: x3 = 1, x2 = 1, x1 = 1, Expected f = 1
        {x3, x2, x1} = 3'b111;
        #10;
        if(f !== 1'b1) begin
            $display("Test case failed: x3=1, x2=1, x1=1, Expected f=1, Got f=%b", f);
            errors = errors + 1;
        end

        // All test cases completed
        if (errors == 0) $display("all test cases passed");

        // Finish the simulation
        $finish;
    end
