module testbench;
    reg clk, in, reset;
    wire [7:0] out_byte;
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_byte(out_byte),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz clock
    end

    // Test cases
    integer errors = 0; // Error counter

    task initialize;
        begin
            reset = 1; 
            #10; 
            reset = 0;
            in = 1; // IDLE state
        end
    endtask

    task send_byte;
        input [7:0] data_byte;
        integer i;
        begin
            in = 0; // Start bit
            #10;
            for (i = 0; i < 8; i = i + 1) begin
                in = data_byte[i]; // Send LSB first
                #10;
            end
            in = 1; // Stop bit
            #10; 
            in = 1; // Return to IDLE
            #10;
        end
    endtask

    task check_output;
        input [7:0] expected_data;
        begin
            if (done !== 1'b1 || out_byte !== expected_data) begin
                errors = errors + 1;
            end
        end
    endtask

    // Perform the test
    initial begin
        initialize();

        // Test case 1: Normal operation
        send_byte(8'b10101010); // Send 0xAA
        check_output(8'b10101010);

        // Test case 2: Testing reset in the middle of transmission
        send_byte(8'b11001100); // Start sending 0xCC
        #15; reset = 1; #10; reset = 0; // Reset during transmission
        #40; // Wait for reset to take effect
        send_byte(8'b11001100); // Resend 0xCC
        check_output(8'b11001100);

        // Test case 3: Stop bit failure
        in = 0; // Start bit for next byte
        #10;
        // Simulate incorrect stop bit scenario
        for (int j = 0; j < 8; j = j + 1) begin
            in = (j % 2 == 0) ? 1 : 0;
            #10;
        end
        #10 in = 0; // Incorrect stop bit
        #10 in = 1; // Should wait for a correct stop bit which never arrives

        #10 send_byte(8'b10100101); // Send 0xA5
        check_output(8'b10100101);

        // Check results
        if (errors === 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
endmodule

