module testbench;
    reg clk, d, ar;
    wire q;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .d(d),
        .ar(ar),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk;  // Generate a clock with 10ns period
    end

    // Test cases
    initial begin
        // Initialize Inputs
        d = 0;
        ar = 0;

        // Reset the D flip flop
        #10;
        ar = 1; #10;
        ar = 0;

        if (q !== 0) begin
            $display("Test Case Failed: Reset functionality");
            $finish;
        end

        // Test Case 1: Check if D flip flop holds the value when clock is triggered
        d = 1; #10;  // Wait for the next positive edge
        if (q !== 1) begin
            $display("Test Case Failed: Check clocking behavior with D=1");
            $finish;
        end

        // Test Case 2: Check reset while D is high
        ar = 1; #10;
        if (q !== 0) begin
            $display("Test Case Failed: Check asynchronous reset");
            $finish;
        end

        ar = 0; d = 0; #10;  // De-assert reset and set D to 0
        if (q !== 0) begin
            $display("Test Case Failed: Check D=0 behavior");
            $finish;
        end

        // Test Case 3: Confirm no change in output when clock is not triggered
        #15;  // Odd delay to ensure no clock edge
        if (q !== 0) begin
            $display("Test Case Failed: Output should not change without clock edge");
            $finish;
        end

        // Test Case 4: Check positive edge trigger feature
        d = 1; #10;
        if (q !== 1) begin
            $display("Test Case Failed: Check positive edge triggering with D=1");
            $finish;
        end

        // All test cases have passed
        $display("all test cases passed");
        $finish;
    end

endmodule

