module testbench;
    reg clk;
    reg reset;
    reg [3:1] s;
    wire fr3;
    wire fr2;
    wire fr1;
    wire dfr;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .s(s),
        .fr3(fr3),
        .fr2(fr2),
        .fr1(fr1),
        .dfr(dfr)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with period of 10 ns
    end

    // Test scenarios
    initial begin
        // Initialize all signals
        reset = 1;
        s = 3'b000;
        #10;
        reset = 0;

        // Scenario 1: All sensors are off
        s = 3'b000;
        #10;
        if (fr3 !== 1'b1 || fr2 !== 1'b1 || fr1 !== 1'b1 || dfr !== 1'b0) $display("Test Case Failed Nowhere");

        // Scenario 2: Water level between lowest sensor and middle
        s = 3'b001;
        #10;
        if (fr3 !== 1'b1 || fr2 !== 1'b1 || fr1 !== 1'b1 || dfr !== 1'b0) $display("Test Case Failed Lower");

        // Scenario 3: Water level between middle and upper sensor
        s = 3'b011;
