module tb_top_module;

    reg clk;
    reg reset;
    reg ena;
    wire pm;
    wire [7:0] hh;
    wire [7:0] mm;
    wire [7:0] ss;

    // Instance of the DUT
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10ns period (100 MHz)
    end

    // Test cases
    initial begin
        // Initialize inputs
        reset = 0;
        ena = 0;

        // Test Case 1: Reset to 12:00 AM
        #100;
        reset = 1; #10;
        reset = 0; #10;

        if (hh !== 8'h12 || mm !== 8'h00 || ss !== 8'h00 || pm !== 1'b0) begin
            $display("Test Case 1 Failed: Reset to 12:00 AM does not set correct values");
            $finish;
        end

        // Test Case 2: Increment seconds
        ena = 1;
        #10; ena = 0; #10; // T+1 second
        if (ss !== 8'h01) begin
            $display("Test Case 2 Failed: Second increment failure");
            $finish;
        end

        // Test Case 3: Increment minutes after 60 seconds
        repeat(59) begin
            ena = 1; #10; ena = 0; #10;
        end
        if (mm !== 8'h01 || ss !== 8'h00) begin
            $display("Test Case 3 Failed: Minute increment failure after 60 seconds");
            $finish;
        end

        // Test Case 4: Increment hours after 60 minutes
        repeat(59) begin
            repeat(59) begin
                ena = 1; #10; ena = 0; #10;
            end
        end
        #10; // Increment to next hour
        if (hh !== 8'h01 || mm !== 8'h00) begin
            $display("Test Case 4 Failed: Hour increment failure after 3600 seconds");
            $finish;
        end

        // Test Case 5: Transition from AM to PM
        repeat(11) begin // Move from 01:00 AM to 12:00 PM
            repeat(59) begin
                repeat(59) begin
                    ena = 1; #10; ena = 0; #10;
                end
            end
            #10; // increment hour
        end
        if (pm !== 1'b1 || hh !== 8'h12) begin
            $display("Test Case 5 Failed: AM to PM transition failure");
            $finish;
        end

        // All test cases passed
        $display("all test cases passed");
    end

endmodule

