module testbench;
    reg clk;
    reg reset;
    wire [3:1] ena;
    wire [15:0] q;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .q(q)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    initial begin
        reset = 1;
        #10;
        reset = 0;
        #1000;
        if (test_BCD_counter() == 1) $display("All test cases passed");
        $finish;
    end

    function automatic integer count_ones(input [15:0] value);
        integer i;
        integer count;
        begin
            count = 0;
            for (i = 0; i < 16; i=i+1) begin
                count = count + value[i];
            }
            return count;
        end
    endfunction

    task check_reset;
        integer i;
        begin
            reset = 1;
            #10;
            reset = 0;
            #10;
            for (i = 0; i <= 15; i = i + 1) begin
                if (q[i] !== 0) begin
                    $display("Reset test failed at time %t", $time);
                end
            end
        end
    endtask

    function automatic integer test_BCD_counter();
        integer i;
        integer prev_ones, current_ones;
        reg correct;
        begin
            correct = 1;
            check_reset();
            for (i = 0; i < 10000; i = i + 1) begin
                // Test if digits wrap correctly and if ena signals are correct
                if (q[3:0] > 9 || q[7:4] > 9 || q[11:8] > 9 || q[15:12] > 9) begin
                    $display("BCD digit overflow error at time %t", $time);
                    correct = 0;
                end

                prev_ones = count_ones(q[3:0]);
                #10;
                current_ones = count_ones(q[3:0]);
                if (prev_ones > current_ones && (!ena[1] || !ena[2] || !ena[3])) begin
                    $display("Enable signal error at time %t", $time);
                    correct = 0;
                end
            end
            return correct;
        end
    endfunction

endmodule

