module testbench;
    reg clk, areset, x;
    wire z;
    integer i;
    reg [31:0] test_number;
    reg [31:0] expected_output;
    reg error_detected;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .areset(areset),
        .x(x),
        .z(z)
    );

    // Clock generation
    always #5 clk = ~clk; // Generate a clock with 10ns period

    // Test cases using initial block
    initial begin
        // Initialize inputs
        clk = 0;
        areset = 1;
        x = 0;
        test_number = 0;
        error_detected = 0;

        // Apply reset
        #10;
        areset = 0;
        #10;
        areset = 1;
        #10;

        // Test case 1: Input 0011 (binary 3), expect output 1101 (binary -3 in 2's complement)
        test_number = 4'b0011;
        expected_output = 4'b1101;
        perform_test(test_number, expected_output);

        // Test case 2: Input 1011 (binary -5), expect output 0101 (binary 5 in 2's complement)
        test_number = 4'b1011;
        expected_output = 4'b0101;
        perform_test(test_number, expected_output);

        // Test case 3: Input 0000, expect output 0000 (binary 0)
        test_number = 4'b0000;
        expected_output = 4'b0000;
        perform_test(test_number, expected_output);

        // Check if any test case failed
        if (!error_detected) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end

    // Test procedure - sends bits of test_number least significant bit first and checks output
    task perform_test;
        input [31:0] number;
        input [31:0] expected;
        reg [31:0] captured_output;
        integer bit_idx;
        begin
            captured_output = 0;
            areset = 0; // Release Reset to start operation
            #10;

            // Send input bits, least significant bit first
            for (bit_idx=0; bit_idx < 4; bit_idx=bit_idx+1) begin
                x = number[bit_idx];
                #10; // Wait for one clock cycle
                captured_output[bit_idx] = z;
            end

            // Assert reset to stop operation
            areset = 1;
            #10;

            // Compare the captured output against the expected output
            if (captured_output !== expected) begin
                error_detected = 1;
                $display("Test failed: Input %b, Expected Output %b, Received %b", number, expected, captured_output);
            end
        end
    endtask

endmodule

