module testbench;
    reg [7:0] a, b;
    wire [7:0] s;
    wire overflow;
    integer num_tests = 0;
    integer num_passed = 0;

    // Instantiate DUT
    top_module dut (.a(a), .b(b), .s(s), .overflow(overflow));

    // Testbench procedure to apply stimulus and check results
    initial begin
        // Test Case 1: No Overflow
        a = 8'd50; b = 8'd70;   // Expected s = 120, overflow = 0
        #10;
        check_results(8'd120, 1'b0);

        // Test Case 2: Positive Overflow
        a = 8'd100; b = 8'd30;  // Expected s should wrap around with overflow
        #10;
        check_results(8'd130, 1'b1);

        // Test Case 3: Negative Overflow
        a = -8'd100; b = -8'd30;  // Expected s should wrap around with overflow
        #10;
        check_results(-8'd130, 1'b1);

        // Test Case 4: No Overflow with Negatives
        a = -8'd50; b = -8'd25;  // Expected s = -75, overflow = 0
        #10;
        check_results(-8'd75, 1'b0);

        // Test Case 5: Edge Case - boundary max int
        a = 8'd127; b = 1;       // Expected s should wrap to minimum with overflow
        #10;
        check_results(-8'd128, 1'b1);

        // Test Case 6: Edge Case - boundary min int
        a = -8'd128; b = -1;     // Expected s should wrap to maximum with overflow
        #10;
        check_results(8'd127, 1'b1);

        // Test Case 7: Overflow with Negative and Positive
        a = 8'd100; b = -8'd150; 
        #10;
        check_results(-8'd50, 1'b0);

        // Display test results
        if (num_passed == num_tests) begin
            $display("all test cases passed");
        end
    end

    // Function to check results and display message
    task check_results;
        input [7:0] expected_sum;
        input expected_overflow;
        begin
            num_tests = num_tests + 1;
            if (s == expected_sum && overflow == expected_overflow) begin
                 num_passed = num_passed + 1;
            end
            else begin
