        slowena = 1;  // Enable counting
        #100;         // Wait for 10 clock cycles
        if (q !== 9) $display("Count error: expected 9, got %d at time %t", q, $time);

        // Check wrapping from 9 to 0
        #10;
        if (q !== 0) $display("Wrap-around error: expected 0, got %d at time %t", q, $time);

        // Check pause functionality
        slowena = 0;
