        reset = 0;

        // Final test - let it run full cycle
        slowena = 1;
        repeat (10) #10;
        if (q !== 9) $display("Final count error: expected 9, got %d at time %t", q, $time);

        // Check wrap-around one last time
        #10;
        if (q !== 0) $display("Final wrap-around error: expected 0, got %d at time %t", q, $time);

        // Check that all test cases have passed
