module testbench;
    reg clk;
    reg resetn;
    reg [3:1] r;
    wire [3:1] g;
    reg err_flag = 0;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .resetn(resetn),
        .r(r),
        .g(g)
    );

    // Clock generation
    always #5 clk = ~clk;

    // Test cases
    initial begin
        clk = 0; 
        resetn = 1; // active low reset
        r = 3'b000; // r[3:1] = 000

        // Assert reset
        resetn = 0;
        #10
        resetn = 1;

        if (g !== 3'b000) begin
            $display("Reset failure: g should be 000");
            err_flag = 1;
        end

        // Test Case 1: No requests, stays in A
        #10;
        r = 3'b000;
        #10;
        if (g !== 3'b000) begin
            $display("Failure in case 1: No requests, output should be 000");
            err_flag = 1;
        end

        // Test Case 2: r1 request, goes to B
        r = 3'b100; // r1 high
        #10;
        if (g !== 3'b100) begin
            $display("Failure in case 2: r1 requests, output should be 100");
            err_flag = 1;
        end

        // Test Case 3: r1 drops, returns to A
        r = 3'b000;
        #10;
        if (g !== 3'b000) begin
            $display("Failure in case 3: r1 drops, output should be 000");
            err_flag = 1;
        end

        // Test Case 4: r2 request, goes to C
        r = 3'b010; // r2 high
        #10;
        if (g !== 3'b010) begin
            $display("Failure in case 4: r2 requests, output should be 010");
            err_flag = 1;
        end

        // Test Case 5: r2 drops, returns to A
        r = 3'b000;
        #10;
        if (g !== 3'b000) begin
            $display("Failure in case 5: r2 drops, output should be 000");
            err_flag = 1;
        end

        // Test Case 6: High priority overriding lower
        r = 3'b110; // r1 and r2 high, r1 has higher priority
        #10;
        if (g !== 3'b100) begin
            $display("Failure in case 6: r1 and r2 request, output should be 100 (priority test)");
            err_flag = 1;
        end

        // Final check
        if (err_flag === 0) begin
            $display("all test cases passed");
        end

        $finish;
    end
