module tb_top_module;

    // DUT inputs and outputs
    reg clk;
    reg a;
    reg b;
    wire out_assign;
    wire out_always_comb;
    wire out_always_ff;

    // Instantiate the Unit Under Test (DUT)
    top_module uut (
        .clk(clk),
        .a(a), 
        .b(b),
        .out_assign(out_assign),
        .out_always_comb(out_always_comb),
        .out_always_ff(out_always_ff)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generating a clock with 10ns period
    end

    // Test stimulus
    initial begin
        // Initialize Inputs
        a = 0;
        b = 0;

        // Wait for global reset
        #100;

        // Test vector 1: a=0, b=0
        a = 0; b = 0; 
        #10; 
        check_outputs;

        // Test vector 2: a=0, b=1
        a = 0; b = 1; 
        #10; 
        check_outputs;

        // Test vector 3: a=1, b=0
        a = 1; b = 0;
        #10;
        check_outputs;

        // Test vector 4: a=1, b=1
        a = 1; b = 1;
        #10;
        check_outputs;

        // Check if all test cases passed
        if (test_passed) $display("all test cases passed");

        // End simulation
        $finish;
    end

    // Variable to keep track if all tests have passed
    reg test_passed = 1'b1;

    // Procedure to check the outputs
    task check_outputs;
        begin
            // Check assign and comb outputs immediately
            if(out_assign !== (a ^ b) || out_always_comb !== (a ^ b)) begin
                test_passed = 1'b0;
            end

            // Delay to check the flip-flop output, has one clock cycle delay
            @(posedge clk)
            if(out_always_ff !== (a_pre ^ b_pre)) begin
                test_passed = 1'b0;
            end

            // Store previous values of a and b to verify flip-flop (out_always_ff) output
            a_pre <= a;
            b_pre <= b;
        end
    endtask

    // Variables to store previous values of a and b
    reg a_pre, b_pre;

endmodule

