module testbench;

reg clk, areset, bump_left, bump_right, ground, dig;
wire walk_left, walk_right, aaah, digging;

top_module DUT (
    .clk(clk),
    .areset(areset),
    .bump_left(bump_left),
    .bump_right(bump_right),
    .ground(ground),
    .dig(dig),
    .walk_left(walk_left),
    .walk_right(walk_right),
    .aaah(aaah),
    .digging(digging)
);

// Clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk;
end

// Test cases
initial begin
    // Initialize all inputs
    areset = 1;
    bump_left = 0;
    bump_right = 0;
    ground = 1;
    dig = 0;
    #10;
    areset = 0;

    // Test 1: Basic walk left and right transition
    // Lemming should initially walk left
    @(negedge clk);
    assert(walk_left == 1 && walk_right == 0 && aaah == 0 && digging == 0);

    // Bump left, should start walking right
    bump_left = 1;
    @(negedge clk);
    #1;
    assert(walk_left == 0 && walk_right == 1);
    bump_left = 0;

    // Bump right, should start walking left
    bump_right = 1;
    @(negedge clk);
    #1;
    assert(walk_left == 1 && walk_right == 0);
    bump_right = 0;

    // Test 2: Falling without ground
    ground = 0;
    @(negedge clk);
    #1;
    assert(aaah == 1 && walk_left == 1); // Falling should not change walking direction

    // Test 3: Resume walking after fall
    ground = 1;
    @(negedge clk);
    #1;
    assert(walk_left == 1 && aaah == 0);

    // Test 4: Digging affecting states
    dig = 1;
    @(negedge clk);
    #1;
    assert(digging == 1 && aaah == 0 && walk_left == 0 && walk_right == 0);

    // Stop digging after ground disappears
    ground = 0;
    @(negedge clk);
    #1;
    assert(digging == 0 && aaah == 1);

    // Resume walking after ground reappears
    ground = 1;
    @(negedge clk);
    #1;
    assert(walk_left == 1 && digging == 0 && aaah == 0);

    // Test Completion
    $display("all test cases passed");
    $finish;
end

// Failure detection system
always @(*) begin
    if ((|{aaah, walk_left, walk_right, digging} === 4'bx) | ({bump_left, bump_right, dig} === 3'b111)) begin
        $display("Invalid input state or output in undefined state.");
        $finish;
    end
end

endmodule

`timescale 1ns / 1ps

module testbench;

    // Inputs to DUT
    reg clk, j, k, areset;
    // Output from DUT
    wire out;

    // Instantiate the DUT
    top_module DUT (
        .clk(clk),
        .j(j),
        .k(k),
        .areset(areset),
        .out(out)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test Cases
    initial begin
        // Initialize Inputs
        j = 0; k = 0; areset = 1; 
        #10;

        areset = 0; // release reset
        // Test Case 1: Remain OFF when j=0
        #10; j = 0;
        if (out !== 0) $display("Test case 1 failed");

        // Test Case 2: Transition to ON when j=1
        #10; j = 1;
        if (out !== 1) $display("Test case 2 failed");

        // Test Case 3: Remain ON when k=0 (j=1)
        #10; k = 0;
        if (out !== 1) $display("Test case 3 failed");

        // Test Case 4: Transition to OFF when k=1
        #10; k = 1;
        if (out !== 0) $display("Test case 4 failed");

        // Test Case 5: Testing async reset
        #10; j = 1; k = 0; areset = 1;
        #10;
        if (out !== 0) $display("Test case 5 failed");

        // All tests passed
        #10; areset = 0; j = 0; k = 0;
        #20; // allow some time for the state to settle
        $display("all test cases passed");

        $finish;
    end

endmodule

