        d = 1;
        #20;
        if (q !== 1) $display("Test case failed at time %t with q = %d", $time, q);

        d = 0;
        #20;
        if (q !== 0) $display("Test case failed at time %t with q = %d", $time, q);

        // Test all scenarios covered
        #10;
        if (q === 0) $display("all test cases passed");

