module testbench;
    reg clk, reset, data, ack;
    wire [3:0] count;
    wire counting, done;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Generate a clock with 10ns period
    end

    // Stimulus
    initial begin
        reset = 1;
        ack = 0;
        data = 0;
        #20; // Wait for reset to take effect

        reset = 0;
        send_pattern_and_delay(4'b1101, 4'd3);
        wait_for_done_and_ack();

        send_pattern_and_delay(4'b1101, 4'd1);
        wait_for_done_and_ack();

        send_pattern_and_delay(4'b1101, 4'd0);
        wait_for_done_and_ack();

        send_pattern_and_delay(4'b1101, 4'd15);
        wait_for_done_and_ack();

        // Check all cases passed
        $display("all test cases passed");
        $finish;
    end

    task send_pattern_and_delay;
        input [3:0] pattern;
        input [3:0] delay;
        integer i;
        begin
            // Send the 1101 pattern
            for (i = 0; i < 4; i = i + 1) begin
                data = pattern[i];
                #10; // Wait a clock cycle
            end
            // Send the delay bits
            for (i = 3; i >= 0; i = i - 1) begin
                data = delay[i];
                #10; // Wait a clock cycle
            end
        end
    endtask

    task wait_for_done_and_ack;
        begin
            // Wait for done to be asserted
            while (!done) begin
                #10;
            end
            ack = 1;
            #20;
            ack = 0;
        end
    endtask

endmodule

