module testbench;
    // Inputs
    reg clk;
    reg reset;

    // Outputs
    wire shift_ena;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .shift_ena(shift_ena)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100MHz Clock
    end

    // Test cases
    initial begin
        $display("[TEST] Begin tests.");
        // Test Case 1: Reset and observe shift_ena
        reset = 1;
        // Reset is synchronous; apply reset with the clock
        @(posedge clk); #1;
        reset = 0;

        // check shift_ena for 4 cycles
        @(posedge clk);
        begin
            if (shift_ena !== 1) $display("[FAIL] Shift enable should be high at clk 1.");
        end
        @(posedge clk);
        begin
            if (shift_ena !== 1) $display("[FAIL] Shift enable should be high at clk 2.");
        end
        @(posedge clk);
        begin
            if (shift_ena !== 1) $display("[FAIL] Shift enable should be high at clk 3.");
        end
        @(posedge clk);
        begin
            if (shift_ena !== 1) $display("[FAIL] Shift enable should be high at clk 4.");
        end
        @(posedge clk);
        begin
            if (shift_ena !== 0) $display("[FAIL] Shift enable should be low at clk 5.");
        end

        // Remaining cycles
        repeat (10) @(posedge clk);
        begin
            if (shift_ena !== 0) $display("[FAIL] Shift enable should stay low after 4 cycles.");
        end

        // Reset again test
        @(posedge clk);
        reset = 1; @(posedge clk); #1; reset = 0; @(posedge clk);
        if (shift_ena !== 1) $display("[FAIL] Shift enable should be high at reset re-trigger.");

        // Log upon successful completion of all tests without failures if no failures have been posted
        if (!$test$plusargs("FAIL")) begin
            $display("all test cases passed");
        end
        $finish;  // End the simulation
    end
endmodule

