module tb_top_module;

reg clk;
reg reset;
reg w;
wire z;

// Instantiate the Device Under Test (DUT)
top_module DUT (
    .clk(clk),
    .reset(reset),
    .w(w),
    .z(z)
);

// Clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk; // 100 MHz Clock
end

// Initial test cases setup
initial begin
    // Monitor output
    $monitor("Time = %t, clk = %b, reset = %b, w = %b, z = %b", $time, clk, reset, w, z);

    // Reset sequence
    reset = 1'b1;
    w = 1'b0;
    #10;   // Wait for 10ns
    reset = 1'b0;

    // Stimulus
    // Test transition A(0) -> A(0)
    w = 1'b0;
    #10; // Checking self loop in A
    test_z(1'b0, "Test A->A");

    // Test transition A(0) -> B(0)
    w = 1'b1;
    #10;
    test_z(1'b0, "Test A->B");

    // Test transition B(0) -> D(0)
    w = 1'b0;
    #10;
    test_z(1'b0, "Test B->D");

    // Test transition D(0) -> A(0)
    w = 1'b0;
    #10;
    test_z(1'b0, "Test D->A");

    // Test transition A(0) -> B(0)
    w = 1'b1;
    #10;
    test_z(1'b0, "Test A->B");

    // Test transition B(0) -> C(0)
    w = 1'b1;
    #10;
    test_z(1'b0, "Test B->C");

    // Test transition C(0) -> E(1)
    w = 1'b1;
    #10;
    test_z(1'b1, "Test C->E");

    // Test transition E(1) -> E(1)
    w = 1'b1;
    #10;
    test_z(1'b1, "Test E->E");

    // Complete the Test
    #10;
    if (error_count == 0) begin
        $display("all test cases passed");
    end
    $finish;
end

integer error_count = 0;

task test_z;
    input expected_z;
    input [127:0] testname;
    begin
        if (z !== expected_z) begin
            $display("Error in %s, expected z=%b, got z=%b", testname, expected_z, z);
            error_count = error_count + 1;
        end
    end
endtask

endmodule

