    integer i, j, k, l, error_count = 0;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .a(a), 
        .b(b),
        .c(c),
        .d(d),
        .out(out)
    );

    // Test vector input and expected outputs based on the K-map
    reg [3:0] test_inputs[15:0];  // 4 inputs corresponding to {a, b, c, d}
    reg expected_outputs[15:0];   // Expected output for each combination of inputs

    initial begin
        // Initialize test cases
        test_inputs[0]  = 4'b0000; expected_outputs[0]  = 1;
        test_inputs[1]  = 4'b0001; expected_outputs[1]  = 1;
        test_inputs[2]  = 4'b0011; expected_outputs[2]  = 0;
        test_inputs[3]  = 4'b0010; expected_outputs[3]  = 1;
        test_inputs[4]  = 4'b0100; expected_outputs[4]  = 1;
        test_inputs[5]  = 4'b0101; expected_outputs[5]  = 0;
        test_inputs[6]  = 4'b0111; expected_outputs[6]  = 0;
        test_inputs[7]  = 4'b0110; expected_outputs[7]  = 1;
        test_inputs[8]  = 4'b1100; expected_outputs[8]  = 0;
        test_inputs[9]  = 4'b1101; expected_outputs[9]  = 1;
        test_inputs[10] = 4'b1111; expected_outputs[10] = 1;
        test_inputs[11] = 4'b1110; expected_outputs[11] = 1;
        test_inputs[12] = 4'b1000; expected_outputs[12] = 1;
        test_inputs[13] = 4'b1001; expected_outputs[13] = 1;
        test_inputs[14] = 4'b1011; expected_outputs[14] = 0;
        test_inputs[15] = 4'b1010; expected_outputs[15] = 0;

        // Test all cases
        for (i = 0; i < 16; i = i + 1) begin
            {a, b, c, d} = test_inputs[i];  // Apply test inputs
