  initial begin
    clk = 0;
    forever #5 clk = ~clk; // Clock period = 10 ns
  end

  // Stimulus and Test Vector
  initial begin
    // Initialize Inputs
    reset = 1;
    #10; // Wait for the reset to take effect

    reset = 0;
    #10; // Start the counter

    // Check for reset functionality
    @(posedge clk);
    reset = 1;
    @(posedge clk);
    if (q !== 4'b0000) begin
      $display("Reset test failed.");
      $finish;
    end

    reset = 0;

    // Check if counter counts correctly from 0 to 15
    for (integer i = 0; i < 16; i++) begin
      @(posedge clk);
      if (q !== i[3:0]) begin
        $display("Counting test failed at %d, where q is %d.", i, q);
        $finish;
      end
    end

    // Check if counter wraps around to 0 after 15
    @(posedge clk);
    if (q !== 4'b0000) begin
      $display("Wrap around test failed.");
      $finish;
    end

    // If all tests pass
    $display("all test cases passed");
  end
endmodule

