module testbench;
    reg a, b, c, d, e;
    wire [24:0] out;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .a(a),
        .b(b),
        .c(c),
        .d(d),
        .e(e),
        .out(out)
    );

    // Expected output function for verification
    function [24:0] expected_output;
        input a, b, c, d, e;
        begin
            expected_output[24] = ~a ^ a;
            expected_output[23] = ~a ^ b;
            expected_output[22] = ~a ^ c;
            expected_output[21] = ~a ^ d;
            expected_output[20] = ~a ^ e;
            expected_output[19] = ~b ^ a;
            expected_output[18] = ~b ^ b;
            expected_output[17] = ~b ^ c;
            expected_output[16] = ~b ^ d;
            expected_output[15] = ~b ^ e;
            expected_output[14] = ~c ^ a;
            expected_output[13] = ~c ^ b;
            expected_output[12] = ~c ^ c;
            expected_output[11] = ~c ^ d;
            expected_output[10] = ~c ^ e;
            expected_output[9] = ~d ^ a;
            expected_output[8] = ~d ^ b;
            expected_output[7] = ~d ^ c;
            expected_output[6] = ~d ^ d;
            expected_output[5] = ~d ^ e;
            expected_output[4] = ~e ^ a;
            expected_output[3] = ~e ^ b;
            expected_output[2] = ~e ^ c;
            expected_output[1] = ~e ^ d;
            expected_output[0] = ~e ^ e;
        end
    endfunction

    // Initialize stimulus and compare results
    initial begin
        integer i;
        reg [24:0] expected;
        reg passed;

        // Test vector for inputs
        reg [4:0] test_vector[0:31];

        // Generate all possible combinations for test vectors
        for (i = 0; i < 32; i++) begin
            test_vector[i] = i[4:0];
        end

        passed = 1; // Assume tests will pass unless a failure is found

        for (i = 0; i < 32; i++) begin
            a = test_vector[i][4];
            b = test_vector[i][3];
            c = test_vector[i][2];
            d = test_vector[i][1];
            e = test_vector[i][0];

            // Calculate expected result based on defined function
            expected = expected_output(a, b, c, d, e);

            #1; // Wait time for paths to settle, before checking the output

            // Check if actual output matches expected output
            if (out !== expected) begin
                passed = 0; // Mark as failed if any mismatch occurs
                $display("Test case failed at vector %0d: a=%b, b=%b, c=%b, d=%b, e=%b. Expected: %0b, Got: %0b", i, a, b, c, d, e, expected, out);
            end
        end

        if (passed)
            $display("all test cases passed");
    end
endmodule

