    reg [15:0] error_count = 0;

    // DUT instantiation
    top_module dut (
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test cases
    initial begin
        // Initialize signals
        areset = 1; bump_left = 0; bump_right = 0; ground = 1;
        #10 areset = 0;  // Release reset

        // Test case 0: Basic function test, start walking left
        test_id = 0;
        #10;
        if (!(walk_left && !walk_right && !aaah)) error_count = error_count + 1;

        // Test case 1: Bump on the left, should start walking right
        test_id = 1;
        bump_left = 1; bump_right = 0;
        #10 bump_left = 0;
        if (!(walk_right && !walk_left && !aaah)) error_count = error_count + 1;

        // Test case 2: Bump on the right, should start walking left
        test_id = 2;
        bump_right = 1;
        #10 bump_right = 0;
        if (!(walk_left && !walk_right && !aaah)) error_count = error_count + 1;

        // Test case 3: No ground, should say "aaah!"
        test_id = 3;
        ground = 0;
        #10;
        if (!aaah || (walk_left && walk_right)) error_count = error_count + 1;

        // Test case 4: Ground returns, continue walking left
        test_id = 4;
        ground = 1;
        #10;
        if (!(walk_left && !walk_right && !aaah)) error_count = error_count + 1;

        // Test case 5: Double bump, should walk right
        test_id = 5;
        bump_left = 1; bump_right = 1;
        #10 bump_left = 0; bump_right = 0;
        if (!(walk_right && !walk_left && !aaah)) error_count = error_count + 1;

        // Check if all test cases passed
        if (error_count == 0) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end
