                errors = errors + 1;
            end
        end
    endtask

    // Tests
    initial begin
        // Apply test cases based on the Karnaugh map
        perform_test(1'b0, 1'b0, 1'b0, 1'b0); // 000 -> 0
        perform_test(1'b0, 1'b0, 1'b1, 1'b1); // 001 -> 1
        perform_test(1'b0, 1'b1, 1'b1, 1'b1); // 011 -> 1
