        perform_test(1'b1, 1'b0, 1'b1, 1'b1); // 101 -> 1
        perform_test(1'b1, 1'b1, 1'b1, 1'b1); // 111 -> 1
        perform_test(1'b1, 1'b1, 1'b0, 1'b1); // 110 -> 1

