    integer j;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .a(a),
        .b(b),
        .c(c),
        .d(d),
        .e(e),
        .f(f),
        .g(g),
        .h(h),
        .i(i_data),
        .sel(sel),
        .out(out)
    );

    initial begin
        // Initialize Inputs
        a = 16'hAAAA;
        b = 16'hBBBB;
        c = 16'hCCCC;
        d = 16'hDDDD;
        e = 16'hEEEE;
        f = 16'hFFFF;
        g = 16'h1111;
        h = 16'h2222;
        i_data = 16'h3333;
        sel = 4'h0;

        // Wait 100 ns for global reset to finish
        #100;

        // Stimulus here
        $display("Starting test cases");
        for (j = 0; j <= 15; j = j + 1) begin
            sel = j;
            #10;
            case(sel)
                4'h0: if (out !== a) $display("Test case failed for sel=0");
                4'h1: if (out !== b) $display("Test case failed for sel=1");
                4'h2: if (out !== c) $display("Test case failed for sel=2");
                4'h3: if (out !== d) $display("Test case failed for sel=3");
                4'h4: if (out !== e) $display("Test case failed for sel=4");
                4'h5: if (out !== f) $display("Test case failed for sel=5");
                4'h6: if (out !== g) $display("Test case failed for sel=6");
                4'h7: if (out !== h) $display("Test case failed for sel=7");
                4'h8: if (out !== i_data) $display("Test case failed for sel=8");
                4'h9, 4'hA, 4'hB, 4'hC, 4'hD, 4'hE, 4'hF: if (out !== 16'hFFFF) $display("Test case failed for sel=%h", sel);
                default: $display("Invalid sel");
            endcase
        end

        if (!$ferror) $display("all test cases passed");
    end
endmodule

