module tb_top_module;

    reg [254:0] in;
    wire [7:0] out;
    integer i;
    reg [7:0] expected_out;
    reg test_failed;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .out(out)
    );

    // Task to compute the expected population count
    task compute_expected;
        input [254:0] vector;
        output [7:0] count;
        integer i;
        begin
            count = 0;
            for (i = 0; i < 255; i = i + 1) begin
                count = count + vector[i];
            end
        end
    endtask

    // Initial block for test cases
    initial begin
        // Initialize test status
        test_failed = 0;

        // Test case 1: All zeros
        in = 255'b0;
        #10;
        compute_expected(in, expected_out);
        if (out !== expected_out) begin
            test_failed = 1;
        end

        // Test case 2: All ones
        in = {255{1'b1}};
        #10;
        compute_expected(in, expected_out);
        if (out !== expected_out) begin
            test_failed = 1;
        end

        // Test case 3: Alternate ones and zeros
        in = 255'b101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101;
        #10;
        compute_expected(in, expected_out);
        if (out !== expected_out) begin
            test_failed = 1;
        end

        // Final test summary
        if (test_failed === 0) begin
            $display("all test cases passed");
        end

        // Terminating simulation
        $finish;
    end

endmodule

