module testbench;

    reg clk;
    reg load;
    reg [9:0] data;
    wire tc;
    reg [9:0] expected_counter;
    reg testcase_passed;

    top_module dut(
        .clk(clk),
        .load(load),
        .data(data),
        .tc(tc)
    );

    // Generate clock
    always #5 clk = (clk === 1'b0);

    // Test scenario
    initial begin
        clk = 0;
        load = 0;
        data = 0;
        testcase_passed = 1;

        // Initialize the DUT
        #10;
        load = 1;
        data = 10'd500;
        #10 load = 0;

        // Check counter behavior
        expected_counter = 10'd500;
        while(expected_counter > 0) begin
            #10;
            if (tc === 1'b1 && expected_counter != 0) begin
                testcase_passed = 0;
            end

            if (load === 0)
                expected_counter = expected_counter - 1;
        end

        // Check if tc asserts correctly at 0
        #10;
        if (tc !== 1'b1) begin
            testcase_passed = 0;
        end

        // Reload the counter during operation
        load = 1;
        data = 10'd300;
        #10 load = 0;
        expected_counter = 10'd300;
        while(expected_counter > 0) begin
            #10;
            if (tc === 1'b1 && expected_counter != 0) begin
                testcase_passed = 0;
            end

            if (load === 0)
                expected_counter = expected_counter - 1;
        end

        // Final tc assertion check
        #10;
        if (tc !== 1'b1) begin
            testcase_passed = 0;
        end

        // Ending the testbench
        #10;
        if(testcase_passed)
            $display("all test cases passed");

        $finish;
    end

endmodule

