module tb_top_module;
    // Inputs
    reg [6:1] y;
    reg w;

    // Outputs
    wire Y2;
    wire Y4;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .y(y),
        .w(w),
        .Y2(Y2),
        .Y4(Y4)
    );

    // Test vectors
    reg [6:1] test_y[0:10]; // State encodings for test
    reg test_w[0:10];       // Input conditions for w
    reg expected_Y2[0:10];  // Expected results for Y2
    reg expected_Y4[0:10];  // Expected results for Y4

    integer i; // Loop variable
    reg all_tests_passed;

    initial begin
        // Initialize all test cases
        test_y[0] = 6'b000001; test_w[0] = 0; expected_Y2[0] = 1'b0; expected_Y4[0] = 1'b0; // A --0--> B
        test_y[1] = 6'b000001; test_w[1] = 1; expected_Y2[1] = 1'b0; expected_Y4[1] = 1'b0; // A --1--> A
        test_y[2] = 6'b000010; test_w[2] = 0; expected_Y2[2] = 1'b0; expected_Y4[2] = 1'b1; // B --0--> C
        test_y[3] = 6'b000010; test_w[3] = 1; expected_Y2[3] = 1'b0; expected_Y4[3] = 0;    // B --1--> D
        test_y[4] = 6'b000100; test_w[4] = 0; expected_Y2[4] = 1'b0; expected_Y4[4] = 0;    // C --0--> E
        test_y[5] = 6'b000100; test_w[5] = 1; expected_Y2[5] = 1'b0; expected_Y4[5] = 0;    // C --1--> D
        test_y[6] = 6'b001000; test_w[6] = 0; expected_Y2[6] = 1'b0; expected_Y4[6] = 0;    // D --0--> F
        test_y[7] = 6'b001000; test_w[7] = 1; expected_Y2[7] = 1'b0; expected_Y4[7] = 1'b0; // D --1--> A
        test_y[8] = 6'b010000; test_w[8] = 0; expected_Y2[8] = 1'b0; expected_Y4[8] = 1'b0; // E --0--> E
        test_y[9] = 6'b010000; test_w[9] = 1; expected_Y2[9] = 1'b0; expected_Y4[9] = 0;    // E --1--> D
        test_y[10] = 6'b100000; test_w[10] = 0; expected_Y2[10] = 1'b0; expected_Y4[10] = 1'b1; // F --0--> C
        test_y[11] = 6'b100000; test_w[11] = 1; expected_Y2[11] = 1'b0; expected_Y4[11] = 0;     // F --1--> D

        all_tests_passed = 1; // Assume all tests will pass initially

        // Apply each test vector
        for (i = 0; i < 12; i = i + 1) begin
            y = test_y[i];
            w = test_w[i];
            #10; // Wait for the output to settle

            // Check results and update pass status
            if (Y2 !== expected_Y2[i] || Y4 !== expected_Y4[i]) begin
                all_tests_passed = 0; // Set flag on failure
            end
        end

        // Display results based on the test summary
        if (all_tests_passed == 1) begin
            $display("all test cases passed");
        end

        $finish; // End simulation
    end

endmodule

