  reg clk, reset, data, done_counting, ack;
  wire shift_ena, counting, done;

  // Instantiate the Device Under Test (DUT)
  top_module DUT (
    .clk(clk),
    .reset(reset),
    .data(data),
    .shift_ena(shift_ena),
    .counting(counting),
    .done_counting(done_counting),
    .done(done),
    .ack(ack)
  );

  // Clock generation
  initial begin
    clk = 0; 
    forever #5 clk = ~clk;  // Clock period of 10ns
  end

  // Test sequence
  initial begin
    // Initialize Inputs
    reset = 1; data = 0; done_counting = 0; ack = 0;

    // Reset the DUT
    #10; 
    reset = 0;

    // Test Case 1: Check if sequence 1101 starts the timer
    #20 data = 1;
    #10 data = 1;
    #10 data = 0;
    #10 data = 1;

    // Test if shift_ena is asserted for exactly 4 clock cycles
    #10 if (shift_ena != 1'b1) $display("Test Case 1 Failed"); // Checking shift_ena
    #10 if (shift_ena != 1'b1) $display("Test Case 1 Failed");
    #10 if (shift_ena != 1'b1) $display("Test Case 1 Failed");
    #10 if (shift_ena != 1'b1) $display("Test Case 1 Failed");
    #10 if (shift_ena != 1'b0) $display("Test Case 1 Failed"); // Should stop after 4 cycles

    // Test Case 2: Check if the counting is asserted after shift_ena
    if (counting != 1'b1) $display("Test Case 2 Failed"); // Checking counting

    // Simulating counter done
    #50 done_counting = 1;

    // Test Case 3: Check done assertion after done_counting is high
    #10 if (done != 1'b1) $display("Test Case 3 Failed"); 

    // Acknowledge the done
    ack = 1;

    // Test Case 4: Waiting for Reset after Ack
    #20 if (done != 1'b0) $display("Test Case 4 Failed");
    if (counting != 1'b0) $display("Test Case 4 Failed");

    // No failures reported means all test cases passed
    $display("all test cases passed");

    // Finish testing
    #30 $finish;
  end

