module testbench;

    // Inputs
    reg clk;
    reg in;
    reg reset;

    // Outputs
    wire [7:0] out_byte;
    wire done;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_byte(out_byte),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100MHz clock
    end

    // Data for testing
    reg [20:0] test_vector[0:4];
    integer i, error_count;

    initial begin
        // Initialize
        in = 1;
        reset = 1;
        error_count = 0;

        // Test vectors
        test_vector[0] = 21'b110010100011111111100;  // 0xC5 (197) data without error
        test_vector[1] = 21'b111001100100111111100;  // Incorrect stop bit (re-sync required)
        test_vector[2] = 21'b110101010101111111100;  // 0x55 (85) data without error
        test_vector[3] = 21'b101010101010111111100;  // Incorrect start bit (invalid data)
        test_vector[4] = 21'b110111000011111111100;  // 0xE3 (227) data without error

        // Apply reset
        #10;
        reset = 0;
        #10;
        reset = 1;

        // Start test
        for (i = 0; i < 5; i = i + 1) begin
            #100; // Wait some time between transmissions
            send_bits(test_vector[i]);
        end

        #100;
        if (error_count == 0)
            $display("all test cases passed");
    end

    task send_bits;
        input [20:0] bits;
        integer j;
        begin
            for (j = 20; j >= 0; j = j - 1) begin
                in = bits[j];
                #10; // Bit period
            end
        end
    endtask

    // Verification process
    always @(posedge clk) begin
        if (done && (out_byte !== expected_data(i))) begin
            $display("Test case %d failed: expected %h, got %h", i, expected_data(i), out_byte);
            error_count = error_count + 1;
        end
        // Check if all test cases have been processed to prevent infinite looping
        if (i >= 5) begin
            $display("Ending simulation due to completion of all test cases.");
            $finish; // Ends the simulation
        end
    end

    function automatic [7:0] expected_data;
        input integer index;
        case (index)
            0: expected_data = 8'hC5;
            2: expected_data = 8'h55;
            4: expected_data = 8'hE3;
            default: expected_data = 8'hXX;  // Don't care
        endcase
    endfunction
endmodule

