    reg [7:0] a, b;
    wire [7:0] s;
    wire overflow;

    top_module DUT (
        .a(a),
        .b(b),
        .s(s),
        .overflow(overflow)
    );

    initial begin
        // Test Case 1: No overflow
        a = 8'd50; b = 8'd70;
        #10;
        if (s !== (a + b) | overflow !== 0) fail_test();

        // Test Case 2: Positive overflow
        a = 8'd100; b = 8'd60;
        #10;
        if (s !== (a + b) | overflow !== 1) fail_test();

        // Test Case 3: Negative overflow
        a = -8'd100; b = -8'd60;
        #10;
        if (s !== (a + b) | overflow !== 1) fail_test();

        // Test Case 4: Edge case, no overflow
        a = 8'd127; b = 8'd1;
        #10;
        if (s !== (a + b) | overflow !== 1) fail_test();

        // Test Case 5: Edge case with negatives, no overflow
        a = -8'd128; b = -8'd1;
        #10;
        if (s !== (a + b) | overflow !== 1) fail_test();

        // Test Case 6: Zero + Negative, no overflow
        a = 0; b = -8'd5;
        #10;
        if (s !== (a + b) | overflow !== 0) fail_test();

        // Test Case 7: Zero + Positive, no overflow
        a = 0; b = 8'd5;
        #10;
        if (s !== (a + b) | overflow !== 0) fail_test();

        // All tests passed
        all_tests_passed();

    end

    task fail_test;
        begin
            // Uncomment the next line if debugging info is needed
            // $display("Test failed with a = %d, b = %d, s = %d, overflow = %d", a, b, s, overflow);
            $finish;
        end
    endtask

    task all_tests_passed;
        begin
            $display("all test cases passed");
            $finish;
        end
    endtask

endmodule

