module testbench;
    reg clk, reset, in;
    wire disc, flag, err;

    top_module DUT (
        .clk(clk),
        .reset(reset),
        .in(in),
        .disc(disc),
        .flag(flag),
        .err(err)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever begin
            #5 clk = !clk;  // Clock period of 10 ns
        end
    end

    // Test cases
    initial begin
        // Initialize inputs
        reset = 1; in = 0;
        #10;  // Wait for one clock cycle after reset to apply test input
        reset = 0;

        // Test Sequence 1: No flags, discard, or errors
        @(posedge clk) in = 0;
        @(posedge clk) in = 1;  // Start of potential flag/discard/error
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 0;  // normal sequence
        @(posedge clk) in = 0;

        // Test Sequence 2: Flag (01111110)
        @(posedge clk) in = 0;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 0;
        @(posedge clk) in = 0;

        // Test Sequence 3: Discard (0111110)
        @(posedge clk) in = 0;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 0;
        @(posedge clk) in = 1;  // sequence break
        @(posedge clk) in = 0;

        // Test Sequence 4: Error (01111111)
        @(posedge clk) in = 0;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 1;
        @(posedge clk) in = 0;

        // Wait for outputs and check all test scenarios
        repeat (20) @(posedge clk);

        // Check if all outputs are as expected throughout the test
        if (~disc & ~flag & ~err) begin // Adapt based on your specific checking mechanism
            $display("all test cases passed");
        end

        $finish; // Ensure simulation terminate to avoid running indefinitely
    end
endmodule

