module testbench;
    reg clk;
    reg areset;
    reg train_valid;
    reg train_taken;
    wire [1:0] state;
    reg [1:0] previous_state; // Adding previous_state as a reg to store previous state

    top_module dut(
        .clk(clk),
        .areset(areset),
        .train_valid(train_valid),
        .train_taken(train_taken),
        .state(state)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test stimulus
    initial begin
        // Initialization
        areset = 1;
        train_valid = 0;
        train_taken = 0;
        #10;
        areset = 0;
        #10;

        // Test Case 1: Reset check
        areset = 1;
        #10;
        areset = 0;
        if (state !== 2'b01) $display("Test Case 1 Failed: After reset, state is not 2'b01.");

        // Test Case 2: Increment to max test
        train_valid = 1;
        train_taken = 1;
        #10; // Increment to 2
        #10; // Increment to 3
        train_taken = 1;
        #10; // Try incrementing beyond 3, should saturate at 3
        if (state !== 2'b11) $display("Test Case 2 Failed: Counter did not saturate at max (3).");

        // Test Case 3: Decrement to min test
        train_valid = 1;
        train_taken = 0;
        #10; // Decrement to 2
        #10; // Decrement to 1
        previous_state = state;
        #10; // Decrement to 0
        train_taken = 0;
        #10; // Try decrementing beyond 0, should saturate at 0
        if (state !== 2'b00) $display("Test Case 3 Failed: Counter did not saturate at min (0)."); 

        // Test Case 4: Hold value test
        train_valid = 0;
        train_taken = 1;
        if(previous_state !== state) $display("Test Case 4 Failed: Counter value should hold when train_valid is 0.");

        // Check if all test cases have passed
        if ($fopen("display.out")) begin
            $display("All test cases passed");
        end

        $finish;
    end

endmodule

