module testbench;
    reg clk, areset, x;
    wire z;
    reg [31:0] current_value;
    reg [31:0] test_vector;
    integer i, errors;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .areset(areset),
        .x(x),
        .z(z)
    );

    // Clock generation
    always #5 clk = ~clk;

    initial begin
        clk = 0;
        areset = 1;
        x = 0;
        errors = 0;
        current_value = 0;
        test_vector = 32'b0;

        // Reset the DUT
        #10 areset = 0;
        #10 areset = 1;
        #10 areset = 0;

        // Test vectors
        // Example: Input = 3 (011 in binary, LSB first -> 110); 2's complement of 3 is 1101 (D in hex)
        test_vector = 32'b011; // Three-bit input as example, real test vectors should be adjusted accordingly.
        #10 for (i = 0; i < 32; i = i + 1) begin
            x = test_vector[i];
            #10;
        end

        // Assert reset to end the operation
        #10 areset = 1;
        #10 current_value = {current_value[30:0], z};

         // Reset for next operation
        #10 areset = 0;
        #10 areset = 1;
        #10 areset = 0;

        // Test vector for zero
        test_vector = 32'b0; // Zero input
        #10 for (i = 0; i < 32; i = i + 1) begin
            x = test_vector[i];
            #10;
        end

        // Assert reset to end the operation
        #10 areset = 1;
        #10 current_value = {current_value[30:0], z};

        // Reset for next operation
        #10 areset = 0;
        #10 areset = 1;
        #10 areset = 0;

        // Assert that current_value is the 2's complement of the input.
        // Calculate expected value for each input case used above.
        if(current_value[2:0] !== 3'b101 || current_value[34:32] !== 3'b000) begin
            errors = errors + 1;
        end

        // Final output based on error
        if (errors == 0) begin
           $display("all test cases passed");
        end

        $finish;
    end
endmodule

