module testbench;
    reg [3:0] in;
    wire [1:0] pos;
    integer i;
    reg [1:0] expected_pos;
    reg error = 0;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .in(in),
        .pos(pos)
    );

    // Check the priority encoder functionality
    initial begin
        // Test cases for all possible 4-bit combinations
        for (i = 0; i < 16; i = i + 1) begin
            in = i; // Apply input
            #10; // Wait for output to settle

            // Determine expected output position manually
            case (in)
                4'b0001: expected_pos = 2'd0;
                4'b0010: expected_pos = 2'd1;
                4'b0100: expected_pos = 2'd2;
                4'b1000: expected_pos = 2'd3;
                4'b0011: expected_pos = 2'd0;
                4'b0101: expected_pos = 2'd0;
                4'b1001: expected_pos = 2'd0;
                4'b0110: expected_pos = 2'd1;
                4'b1010: expected_pos = 2'd1;
                4'b1100: expected_pos = 2'd2;
                4'b0111: expected_pos = 2'd0;
                4'b1011: expected_pos = 2'd0;
                4'b1101: expected_pos = 2'd0;
                4'b1110: expected_pos = 2'd0;
                4'b1111: expected_pos = 2'd0;
                default: expected_pos = 2'd0;
            endcase

            // Compare device output with expected output
            if (pos !== expected_pos) begin
                $display("Test case failed for input: %b. Expected %b, got %b", in, expected_pos, pos);
                error = 1;
            end
        end

        // Check and display if all test cases passed
        if (!error) begin
            $display("all test cases passed");
        end

        $finish; // End simulation
    end
endmodule

