module testbench;

    reg x3, x2, x1;
    wire f;

    // instantiate the DUT (device under test)
    top_module DUT (
        .x3(x3),
        .x2(x2),
        .x1(x1),
        .f(f)
    );

    integer i;
    reg [2:0] test_cases[0:7];
    reg expected[0:7];
    reg test_result;
    initial begin
        test_cases[0] = 3'b000;
        test_cases[1] = 3'b001;
        test_cases[2] = 3'b010;
        test_cases[3] = 3'b011;
        test_cases[4] = 3'b100;
        test_cases[5] = 3'b101;
        test_cases[6] = 3'b110;
        test_cases[7] = 3'b111;

        expected[0] = 0;
        expected[1] = 0;
        expected[2] = 1;
        expected[3] = 1;
        expected[4] = 0;
        expected[5] = 1;
        expected[6] = 0;
        expected[7] = 1;

        test_result = 1; // assume all test cases pass initially

        for (i = 0; i < 8; i = i + 1) begin
            // apply test inputs
            {x3, x2, x1} = test_cases[i];
            #1; // wait for the outputs to stabilize

            // check the output
            if (f !== expected[i]) begin
                test_result = 0; // mark test as failed
            end
        end

        // after all tests, check if all are passed and display message
